const largestProdInSeries = () => {
  // return only number segments that DO NOT contain a 0 and are at least 13 numbers long
  const hugeNumberArray = "".split(
    ""
  );
  // .split('0')
  // .filter(
  //   function(numEntry) {
  //     return numEntry.length >= 13;
  //   }
  // );

  let maxValue = 0;

  for (let index = 0; index < hugeNumberArray.length; index++) {
    let currSlice = hugeNumberArray.slice(index, index + 13);

    // Test for 0 inclusion; if true; continue;
    if (currSlice.includes("0")) {
      continue;
    }
    // Ensuring currSlice has 13 numbers
    if (currSlice.length === 13) {
      let sliceTotal = currSlice.reduce(function(accum, currVal) {
        return accum * currVal;
      });

      // if the result is larger than the current max, reassign max to result
      if (sliceTotal >= maxValue) {
        maxValue = sliceTotal;
      }
    }
  }

  return maxValue;
};

largestProdInSeries(); //=> 23514624000 [Correct]
